</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic">// @grant GM_setClipboard
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic">// @run-at document-end
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic">// ==/UserScript==
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic"></span>
</span></span><span style="display:flex;"><span>(<span style="color:#c678dd">function</span>() {
</span></span><span style="display:flex;"><span> <span style="color:#98c379">&#39;use strict&#39;</span>;
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span> <span style="color:#c678dd">let</span> <span style="color:#c1abea">customMenu</span> <span style="color:#c7bf54">=</span> <span style="color:#b756ff;font-weight:bold">null</span>; <span style="color:#8a93a5;font-style:italic">// 存储菜单元素
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic"></span>
</span></span><span style="display:flex;"><span> <span style="color:#ef8383">document</span>.<span style="color:#c1abea">addEventListener</span>(<span style="color:#63c381">&#34;contextmenu&#34;</span>, <span style="color:#c678dd">function</span>(<span style="color:#c1abea">event</span>) {
</span></span><span style="display:flex;"><span> <span style="color:#c678dd">let</span> <span style="color:#c1abea">target</span> <span style="color:#c7bf54">=</span> <span style="color:#c1abea">event</span>.<span style="color:#c1abea">target</span>.<span style="color:#c1abea">closest</span>(<span style="color:#63c381">&#34;a&#34;</span>); <span style="color:#8a93a5;font-style:italic">// 只对超链接生效
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic"></span> <span style="color:#c678dd">if</span> (<span style="color:#c7bf54">!</span><span style="color:#c1abea">target</span>) <span style="color:#c678dd">return</span>;
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span> <span style="color:#c1abea">event</span>.<span style="color:#c1abea">preventDefault</span>(); <span style="color:#8a93a5;font-style:italic">// 阻止默认右键菜单
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic"></span> <span style="color:#c1abea">createCustomMenu</span>(<span style="color:#c1abea">target</span>.<span style="color:#c1abea">href</span>, <span style="color:#c1abea">event</span>.<span style="color:#c1abea">pageX</span>, <span style="color:#c1abea">event</span>.<span style="color:#c1abea">pageY</span>);
</span></span><span style="display:flex;"><span> });
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span> <span style="color:#c678dd">function</span> <span style="color:#c1abea">createCustomMenu</span>(<span style="color:#c1abea">link</span>, <span style="color:#c1abea">x</span>, <span style="color:#c1abea">y</span>) {
