</span></span><span style="display:flex;"><span> <span style="color:#8a93a5;font-style:italic">// 点击其他地方隐藏菜单
</span></span></span><span style="display:flex;"><span><span style="color:#8a93a5;font-style:italic"></span> <span style="color:#ef8383">document</span>.<span style="color:#c1abea">addEventListener</span>(<span style="color:#63c381">&#34;click&#34;</span>, <span style="color:#c1abea">removeCustomMenu</span>);
</span></span><span style="display:flex;"><span> }
</span></span><span style="display:flex;"><span>
</span></span><span style="display:flex;"><span> <span style="color:#c678dd">function</span> <span style="color:#c1abea">removeCustomMenu</span>() {
</span></span><span style="display:flex;"><span> <span style="color:#c678dd">if</span> (<span style="color:#c1abea">customMenu</span>) {
</span></span><span style="display:flex;"><span> <span style="color:#c1abea">customMenu</span>.<span style="color:#c1abea">remove</span>();
</span></span><span style="display:flex;"><span> <span style="color:#c1abea">customMenu</span> <span style="color:#c7bf54">=</span> <span style="color:#b756ff;font-weight:bold">null</span>;
</span></span><span style="display:flex;"><span> <span style="color:#ef8383">document</span>.<span style="color:#c1abea">removeEventListener</span>(<span style="color:#63c381">&#34;click&#34;</span>, <span style="color:#c1abea">removeCustomMenu</span>);
</span></span><span style="display:flex;"><span> }
</span></span><span style="display:flex;"><span> }
</span></span><span style="display:flex;"><span>})();
